mapper.update:
UPDATE member
authority = 'A5', withdrawal_date = sysdate,
WHERE member_id = ${memberId}

  			UPDATE member
			   SET authority = 'A2'
			 WHERE member_id = #{memberId}

  			UPDATE member
			   SET authority = 'A1'
			 WHERE member_id = #{memberId}

  			UPDATE member
			   SET punish_count = punish_count + 1
			 WHERE member_id = #{memberId}
