mapper.insert:
				
    		SELECT 'ATF' || (TO_NUMBER(NVL((SUBSTR(MAX(file_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1) 
    		FROM attached_file
    		WHERE SUBSTR(file_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')
		
		
		insert into attached_file
						(
						post_id 
						,origin_img
						,saved_img 
						
							
								,img_type							
							
							
								,img_type
							
						
						,member_id
						,file_id
						,upload_date
						,loading_img
						)
					values
						(
						#{postId}
						,#{originImg}
						,#{savedImg}
						
							
								,#{imgType}							
							
							
								,'U1'
							
						
						,#{memberId}
						,#{fileId}
						,sysdate
						,#{loadingImg}	
						)
