mapper.insert:
 		
			SELECT 'CAT' || (TO_NUMBER(NVL((SUBSTR(MAX(cart_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1)
			FROM cart
			WHERE SUBSTR(cart_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')
		
 	INSERT INTO cart
 				(
 				cart_id
 				,cart_name
 				,post_id
 				,option_id
 				,quantity
 				,price
 				,member_id
 				,reg_date
 				)
 				VALUES
 				(
 				#{cartId}
 				,#{cartName}
 				,#{postId}
 				,#{optionId}
 				,#{quantity}
 				,#{price}
 				,#{memberId}
 				,sysdate
 				)
