mapper.insert:

	SELECT 'PAY' || (TO_NUMBER(NVL((SUBSTR(MAX(payment_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1)
	FROM payment
	WHERE SUBSTR(payment_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')

INSERT INTO payment
			(
				payment_id
				,partner_user_id
				,order_name
				,total_amount
				,tid
				,cid
				,status
				,calculate_status
				,partner_order_id
				,approved_at
			)
			VALUES
			(
				#{paymentId}
				,#{partnerUserId}
				,#{orderName}
				,#{totalAmount}
				,#{tid}
				,#{cid}
				,#{status}
				,#{calculateStatus}
				,#{partnerOrderId}
				,TO_DATE(#{approvedAt},'YYYY-MM-DD HH24:MI:SS')
			)


	SELECT 'PUR' || (TO_NUMBER(NVL((SUBSTR(MAX(order_detail_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1)
	FROM purchase
	WHERE SUBSTR(order_detail_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')

INSERT INTO purchase
			(
				order_detail_id
				,post_id
				,member_id
				,quantity
				,price
				,order_date
				
		    		
		    			,order_status
		    		 
		    		
		    			,order_status
		    		
		    	
				,payment_id
				,cid
				,tid
			)
		    VALUES
		    (
		    	#{orderDetailId}
		    	,#{postId}
		    	,#{memberId}
		    	,#{quantity}
		    	,#{price}
		    	,TO_DATE(#{orderDate},'YYYY-MM-DD HH24:MI:SS')
		    	
		    		
		    			,'B1'
		    		 
		    		
		    			,'B8'
		    		
		    	
		    	,#{paymentId}
		    	,#{cid}
		    	,#{tid}
		    	
		    )
