mapper.insert:

	SELECT 'PAY' || (TO_NUMBER(NVL((SUBSTR(MAX(payment_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1)
	FROM payment
	WHERE SUBSTR(payment_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')

INSERT INTO payment
			(
				payment_id
				,partner_user_id
				,order_name
				,total_amount
				,tid
				,cid
				,status
				,calculate_status
				,partner_order_id
			)
			VALUES
			(
				#{paymentId}
				,#{partnerUserId}
				,#{orderName}
				,#{totalAmount}
				,#{tid}
				,#{cid}
				,#{status}
				,#{calculateStatus}
				,#{partnerOrderId}
			)
