mapper.insert:
        insert into member
        			(member_id,
       				 member_Name,
       				 password,      			        			         			 
        			 nationality,
        			 email,
        			 ZIP_CODE,
        			 ADDRESS,
        			 ADDRESS_DETAIL, 
        			 PHONE_NUMBER,
        			 AUTHORITY,  
        			 LOGIN_TYPE    			  
        			 birth_date,
        			 gender,
        			 SIGN_UP_DATE
        			 )
        values 
			        (  #{memberId} 
			        ,#{memberName}
			        ,#{password}	    
			        ,#{nationality}
			        ,#{email}	   		       
			        ,#{zipCode} 
			        ,#{addr}
			        ,#{addressDetail} 
			        ,#{to}	
			        , 'G1'	
			        , 'L1'
			        ,#{birthDate}		            			        
			        ,#{gender}			     
			        ,sysdate)

mapper.select:

SELECT 		*
  FROM 		member
  WHERE       member_id = #{memberId}
  AND		password = #{password}

mapper.update:
UPDATE member
password = #{password},
address = #{address}, address_detail=#{addressDetail}, email = #{email}, phone_number = #{phoneNumber}
WHERE member_id = #{memberId}
