mapper.insert:

	SELECT 'PKG' || (TO_NUMBER(NVL((SUBSTR(MAX(post_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1)
	FROM package_regist
	WHERE SUBSTR(post_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')

insert into package_regist
			(
				post_id
				,content
				,name
				,price
				,start_date
				,end_date
				,deadline_date
				,max_reservation
				,tour_theme
				,reg_date
			)
		values
			(
				#{postId}
				,#{content}
				,#{name}
				,#{price}
				,#{startDate}
				,#{endDate}
				,#{deadlineDate}
				,#{maxReservation}
				,#{tourTheme}
				,sysdate
			)

mapper.select:
SELECT content, name, price, start_date, end_date, deadline_date, max_reservation, tour_theme,post_id
FROM package_regist
WHERE post_id=#{postId}

SELECT COUNT(*)
FROM package_regist
WHERE writer_id = #{writerId} AND completion = #{completion}
