mapper.insert:

	SELECT 'PKG' || (TO_NUMBER(NVL((SUBSTR(MAX(post_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1)
	FROM package_regist
	WHERE SUBSTR(post_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')

insert into package_regist
			(
				post_id
				,content
				,name
				,price
				,start_date
				,end_date
				,deadline_date
				,max_reservation
				,tour_theme
				,reg_date
				,area_code
				,sigungu_code
				,writer_id
				,completion
			)
		values
			(
				#{postId}
				,#{content}
				,#{name}
				,#{price}
				,#{startDate}
				,#{endDate}
				,#{deadlineDate}
				,#{maxReservation}
				,#{tourTheme}
				,sysdate
				,#{areaCode}
				,#{sigunguCode}
				,#{writerId}
				,'D2'
			)

mapper.select:
SELECT content, name, price, start_date, end_date, deadline_date, max_reservation, tour_theme,post_id
FROM package_regist
WHERE post_id=#{postId}

SELECT *       FROM(SELECT ROWNUM rn, a.*             FROM(
   SELECT *
     FROM review
    WHERE origin_post_id = #{postId}
    ORDER BY review_id DESC
) a			 )        WHERE rn BETWEEN (#{page }*5-4) AND (#{page }*5)

SELECT count(*)
FROM review
WHERE origin_post_id = #{postId}
