mapper.insert:
    	
			SELECT 'CMT' || (TO_NUMBER(NVL((SUBSTR(MAX(comment_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1)
			FROM post_comment
			WHERE SUBSTR(comment_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')
		
    		INSERT INTO post_comment
    					(
    						comment_id
    						, cmtContent
    						, regist_date
    						, post_id
    						, writer_id
    						, cmt_style
    						
    							
    							, null
    							
    							
    							, cmt_parent_id
    							
    						
    						, del_yn
    					)
    				VALUES
    					(
    						#{commentId}
    						, #{cmtContent}
    						, #{registDate}
    						, #{postId}
    						, #{writerId}
    						, #{cmtStyle}
    						
    							
    							, null
    							
    							
    							, #{cmtParentId}
    							
    						
    						, 'N'
    					)
