mapper.insert:

			SELECT 'PNS' || (TO_NUMBER(NVL((SUBSTR(MAX(punish_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1)
			FROM punish
			WHERE SUBSTR(punish_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')

    	INSERT INTO punish(
    		punish_id
		  , punish_date
		  , report_id
		  , punished_id
		  , punish_reason
    	)
    	VALUES(
    		#{punishId}
    	  , sysdate
    	  , #{reportId}
    	  , #{punishedId}
    	  , #{punishReason}	
    	)
