mapper.select:
SELECT COUNT(*)
FROM question
WHERE answer_member_id = #{answerMemberId}

mapper.update:
update question
column1 = #{answerContent}, answer_date = sysdate
WHERE questionId = #{questionId}

update question
answer_contexts = #{answerContexts}, answer_modify_date = sysdate
WHERE answer_member_id = #{answerMemberId}

mapper.select:
 	SELECT COUNT(*)
	FROM question
	WHERE member_id = #{memberId}

mapper.insert:
 		
			SELECT 'QUE' || (TO_NUMBER(NVL((SUBSTR(MAX(question_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1)
			FROM question
			WHERE SUBSTR(question_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')
		
 		INSERT INTO question(
					         QUESTION_ID
					       , MEMBER_ID
					       , QUESTION_DATE
					       , QUESTION_TITLE
					       , QUESTION_CONTENT
					       , QUESTION_TYPE
					       , ANSWER_RESULT
							 ) 
			 VALUES(
					#{questionId}
					, #{memberId}
					, sysdate
					, #{questionTitle}
					, #{questionContent}
					, #{questionType}
					, 'K1'
					)

mapper.update:
UPDATE question
answer_result = 'K2', answer_member_id = #{answerMemberId}, answer_date = sysdate, answer_content = #{answerContent},
WHERE question_id = #{questionId}
