mapper.select:
SELECT COUNT(*)
FROM trip_record
WHERE writer_id = #{writerId}

SELECT COUNT(*)
FROM trip_record
WHERE end_day > sysdate AND trip_save='T2' AND writer_id = #{writerId}

  SELECT COUNT(*)
	FROM trip_record
	WHERE end_day < sysdate AND writer_id = #{writerId}

mapper.insert:

	SELECT 'TRR' || (TO_NUMBER(NVL((SUBSTR(MAX(post_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1)
	FROM trip_record
	WHERE SUBSTR(post_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')

INSERT INTO trip_record
				(
					post_id
					, writer_id
					, trip_title
					, start_day
					, end_day
					, trip_disclose
					, trip_save
					, regist_day
				)
		   VALUES
		        (
					#{postId}
					, #{writerId}
					, #{tripTitle}
					, #{startDay}
					, #{endDay}
					, #{tripDisclose}
					, 'T1'
					, sysdate
		        )


	SELECT 'TRM' || (TO_NUMBER(NVL((SUBSTR(MAX(memo_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1)
	FROM trip_record_memo
	WHERE SUBSTR(memo_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')

INSERT INTO trip_record_memo
				(
					memo_id
					, content
					, memo_date
					, post_id
					, trip_date
				)
		   VALUES
		        (
					#{memoId}
					, #{content}
					, sysdate
					, #{postId}
					, #{tripDate}
		        )


	SELECT 'MAP' || (TO_NUMBER(NVL((SUBSTR(MAX(map_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1)
	FROM map
	WHERE SUBSTR(map_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')

INSERT INTO map
			  (
				map_id
				, map_no
				, map_name
				, map_lat
				, map_lng
				, post_id
				, upload_date
				, trip_date
			  )
		VALUES
			  (
			  	#{mapId}
			  	, #{mapNo}
			  	, #{mapName}
			  	, #{mapLat}
			  	, #{mapLng}
			  	, #{postId}
			  	, sysdate
			  	, #{tripDate}
			  )

mapper.delete:
DELETE FROM map
WHERE post_id = #{postId} 
  AND trip_date = #{tripDate}
