mapper.insert:
SELECT 'trr' || (TO_NUMBER(NVL((SUBSTR(MAX(post_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1) FROM trip_record WHERE SUBSTR(post_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')
INSERT INTO trip_record ( post_id , writer_id
, trip_title
, start_day
, end_day
, trip_disclose , trip_save , regist_day , trip_share ) VALUES ( #{postId} , #{writerId}
, #{tripTitle}
, #{startDay}
, #{endDay}
, #{tripDisclose} , 'T1' , sysdate , #{tripShare} )


	SELECT 'map' || (TO_NUMBER(NVL((SUBSTR(MAX(map_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1)
	FROM map
	WHERE SUBSTR(map_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')

INSERT INTO map
