mapper.insert:

	SELECT 'MAT' || (TO_NUMBER(NVL((SUBSTR(MAX(post_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1)
	FROM mate_recruit
	WHERE SUBSTR(post_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')

INSERT INTO mate_recruit
				(
					post_id
					, mate_title
					, mate_content
					, mate_expense
					, start_day
					, end_day
					, mate_style
					, mate_tag
					, mate_max
					, complition
					, write_date
					, deadline
					, writer_id
					, trip_area
				)
			VALUES
				(
					#{postId}
					, #{mateTitle}
					, #{mateContent}
					, #{mateExpense}
					, #{startDay}
					, #{endDay}
					, #{mateStyle}
					, #{mateTag}
					, #{mateMax}
					, 'C1'
					, sysdate
					, #{deadline}
					, #{writerId}
					, #{tripArea}						
				)
