mapper.update:
UPDATE mate_recruit

	hit = NVL((hit), 0) + 1

WHERE post_id = #{postId}

mapper.insert:

	SELECT 'MAT' || (TO_NUMBER(NVL((SUBSTR(MAX(post_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1)
	FROM mate_recruit
	WHERE SUBSTR(post_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')

INSERT INTO mate_recruit
				(
					post_id
					, mate_title
					, mate_content
					, mate_expense
					, start_day
					, end_day
					, mate_style
					, mate_tag
					, mate_max
					, complition
					, write_date
					, deadline
					, writer_id
					, trip_area
					, room_number
				)
			VALUES
				(
					#{postId}
					, #{mateTitle}
					, #{mateContent}
					, #{mateExpense}
					, #{startDay}
					, #{endDay}
					, #{mateStyle}
					, #{mateTag}
					, #{mateMax}
					, 'C1'
					, sysdate
					, #{deadline}
					, #{writerId}
					, #{tripArea}
					, #{roomNumber}						
				)

mapper.update:
UPDATE mate_recruit
mate_title = #{mateTitle}, mate_content = #{mateContent}, mate_expense = #{mateExpense}, start_day = #{startDay}, end_day = #{endDay}, mate_style = #{mateStyle}, mate_max = #{mateMax}, deadline = #{deadline}, trip_area = #{tripArea},
WHERE post_id = #{postId}

mapper.insert:

	SELECT 'REP' || (TO_NUMBER(NVL((SUBSTR(MAX(report_id), 4)), (TO_CHAR(SYSDATE, 'YYMMDD') || '0000'))) + 1)
	FROM report
	WHERE SUBSTR(report_id, 4, 6) = TO_CHAR(SYSDATE, 'YYMMDD')

INSERT INTO report
				(
					report_id
					, reporter_id
					, post_id
					, punished_id
					, report_date
					, report_reason
					, content
					, result
				)
			VALUES
				(
					#{reportId}
					, #{reporterId}
					, #{postId}
					, #{punishedId}
					, sysdate
					, #{reportReason}
					, #{content}
					, 'P1'
				)

mapper.update:
UPDATE mate_recruit

	APPLY_NUM = NVL((APPLY_NUM), 0) + 1

WHERE post_id = #{postId}

mapper.select:
SELECT apply_num, mate_max
FROM mate_recruit
WHERE post_id = #{postId}

SELECT count(*)
FROM mate_recruit
WHERE writer_id = #{writerId}

SELECT count(*)
FROM mate_recruit r left OUTER JOIN mate_apply a on r.post_id = a.post_id
WHERE a.member_id = #{memberId}

mapper.update:
UPDATE mate_recruit

	apply_num = apply_num - 1

WHERE post_id = #{postId}

mapper.select:
SELECT *
	FROM (SELECT ROWNUM rn, a.*
		FROM (
			SELECT *
			FROM post_comment
			WHERE post_id = #{postId}
			ORDER BY origin_comment_id, regist_date, comment_id
			) a
	)
WHERE rn BETWEEN (#{page}*5-4) AND (#{page}*5)

		SELECT count(*)
        FROM post_comment
        WHERE post_id = #{postId}

SELECT *
	FROM (SELECT ROWNUM rn, a.*
		FROM (
			SELECT *
			FROM post_comment
			WHERE post_id = #{postId}
			ORDER BY origin_comment_id, regist_date, comment_id
		) a
	)
WHERE rn BETWEEN 1 AND 5

mapper.update:
		UPDATE post_comment
        SET content=#{content}
        WHERE comment_id=#{commentId}
